import { Typography } from "@mui/material";
import React from "react";
import { Box } from "@mui/system";
import TextField from '@mui/material/TextField';
import TextareaAutosize from '@mui/material/TextareaAutosize';
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import { FormLabel } from "@mui/material";
import { FormControlLabel } from "@mui/material";
import { FormControl } from "@mui/material";
import { Button } from "@mui/base";
import {customerCart} from "../../Services/dataservice"

function AddressDetails(){



    const [customer, setCustomer]=React.useState({addressType:"",fullAddress:"",city:"",state:""})
    const city=(e)=>{

        setCustomer({...customer,city:e.target.value})

    }
    const state=(e)=>{

        setCustomer({...customer,state:e.target.value})
    }
    const address=(e)=>{

        setCustomer({...customer,fullAddress:e.target.value})

    }

    const addressType =(e)=>{

        setCustomer({...customer,addressType:e.target.value})

    }


    const orderSubmit=()=>{
        // const data=FormData()
        // data.append('addressType',customer.addressType)
        // data.append('fullAddress',customer.fullAddress)
        // data.append('city',customer.city)
        // data.append('state',customer.state)
        customerCart(customer).then((response) => {
            console.log(response)


        }).catch((error) => {
            console.log(error)
        })

    }




    return(
        <Box sx={{
            display:'flex',
            flexDirection:'column',
            width:'60vw',
            marginTop:2,
            height:300,
            justifyContent:'space-between',border:'10px solid red'


        }}>
            <Box sx={{
            display:'flex',
            flexDirection:'row',
            justifyContent:'space-around',
            width:'100%'}}>
            <Typography sx={{position:'relative',right:200}}>Customer Details</Typography>
            <Typography sx={{border:'1px solid black',position:'relative',left:200}}>Address Details</Typography>
            </Box>


            <Box sx={{
            display:'flex',
            flexDirection:'column',
            width:'100%',
            height:300,alignItems:'center'

                }}>

                           <TextareaAutosize
                            aria-label="minimum height"
                            minRows={3}
                            placeholder="Minimum 3 rows"
                            style={{ width: '89%',marginTop:35 }} onChange={address}
                          />



                  <  Box sx={{
                        display:'flex',
                        justifyContent:'space-around',
                        width:'100%',
                        marginTop:2

                    }}>
                            <TextField id="outlined-basic" label="state" variant="outlined" sx={{width:'40%'}} onChange={state}/>
                            <TextField id="outlined-basic" label="city" variant="outlined" sx={{width:'40%'}} onChange={city} />
                            </Box>

                            <FormControl sx={{marginTop:3}} >

                                 <FormLabel id="demo-controlled-radio-buttons-group" sx={{position:'relative',right:450}}>Type</FormLabel>
                                 <RadioGroup sx={{display:'flex',flexDirection:'row',justifyContent:'space-between'}}  onClick={addressType}>
                                        <FormControlLabel value="Home"  control={<Radio />} label="home" sx={{position:'relative',right:300}}  />
                                        <FormControlLabel value="Office" control={<Radio />} label="office" />
                                        <FormControlLabel value="Other" control={<Radio />} label="other" sx={{position:'relative',left:300}}/>


                                </RadioGroup>

                            </FormControl>


            </Box>
            <button style={{width:180,
              position:'relative',
              height:50,
              left:500,
              background:'blue'}} onClick={orderSubmit} >Continue</button>

        </Box>
    )


}
export default AddressDetails
