import Cards from '@mui/material/Box';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import Card from '@mui/material/Card';
import { PinDropSharp } from "@material-ui/icons";
import Home from "../../Pages/Home/Home";
import { Box } from "@mui/system";
import image from '../Books/images/book.png'
import { Button } from "@mui/material";
import { CardActions } from "@material-ui/core";
import { CardMedia } from "@mui/material";
import { Typography } from "@mui/material";
import { CardContent } from "@mui/material";
function Books(props){
    console.log(props)
    //const [image,setImages]=React.useState([])
    const clickMe =()=>{

      props.setGetBook()

      console.log(props.touchImage)
    }

    const [newbook,setNewBook]=React.useState({bookName:'', author:'',quantity:'',price:'' })

    return(
        <Card sx={{ width: 300,height:400 ,marginLeft:2,marginBottom:4}} onClick={clickMe}>
         <Box sx={{width:'100%',height:'60%',backgroundColor:'gray'}}>
         < img src={image} style={{
           width:200,
           height:200,
           position:'relative',
           top:10
         }}/>

        </Box>      
      <CardContent sx={{display:'flex',
      flexDirection:'column',
        justifyContent:'flex-start',
        alignItems:'flex-start'}}>
        <Typography variant="body2" color="text.secondary">
         { props.book.bookName}
        </Typography>
        <Typography variant="body2" color="text.secondary">
         { props.book.author}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {props.book.quantity}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {props.book.price}
        </Typography>
      </CardContent>

    </Card>
    )
}
export default Books
