import { PinDropSharp } from "@material-ui/icons";
import Home from "../../Pages/Home/Home";
import { Box } from "@mui/system";
import image from '../Books/images/book.png'
import { Button } from "@mui/material";
import { CardActions } from "@material-ui/core";
import { CardMedia } from "@mui/material";
import { Typography } from "@mui/material";
import { CardContent } from "@mui/material";
function Books(props){
    //console.log(props)
    //const [image,setImages]=React.useState([])


    const clickMe =()=>{
      //props.getBookFromBooks(bookName,author,quantity,price,id)
      //console.log( bookName,author,quantity,price,id)
      props.getBookFromBooks(newbook)
      console.log(newbook)



    }


    // let bookName = props.book.bookName
    // let author=props.book.author
    // let quantity=props.book.quantity
