    let newbook={bookName: props.book.bookName, author:props.book.author,quantity:props.book.quantity,price: props.book.price,id:props.book._id}

    return(
        <Card sx={{ width: 300,height:400 ,marginLeft:2,marginBottom:4}} onClick={clickMe}>
         <Box sx={{width:'100%',height:'60%',backgroundColor:'gray'}}>
         < img src={image} style={{
           width:200,
           height:200,
           position:'relative',
           top:10
         }}/>

        </Box>      
      <CardContent sx={{display:'flex',
      flexDirection:'column',
        justifyContent:'flex-start',
        alignItems:'flex-start'}}>
        <Typography variant="body2" color="text.secondary">
         { props.book.bookName}
        </Typography>
        <Typography variant="body2" color="text.secondary">
         { props.book.author}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {props.book.quantity}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {props.book.price}
        </Typography>
       < Typography variant="body2" color="text.secondary">
          {props.book._id} 
        </Typography>
      </CardContent>

    </Card>
    )
}
export default Books
