import React from "react";
import Box from '@mui/material/Box';
import Header from "../../components/Header/header";
import Cartlist from "../../components/Cartlist/Cartlist";
import {getCart} from '../../Services/dataservice'
import { OneKPlusOutlined } from "@mui/icons-material";
import { Grid,Paper,Typography,Card,Button } from "@mui/material";
import FormControl from '@mui/material/FormControl';
import MenuItem from '@mui/material/MenuItem';
import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';
import AddressDetails from "../../components/AdressDetails/AdressDetails";


function Cart(){
    const [bookCart,setBookCart]=React.useState([])
    const [displayAddress, setDisplayAddress]=React.useState([true])


    const customerDetails=()=>{
        setDisplayAddress(false)

    }

    const getBooksFromCart=(data)=>{
        getCart().then((response)=>{

                setBookCart(response.data.result)

            }).catch((error)=>{

                console.log(error)

            })

        }
        React.useEffect(()=>{
            //console.log(getBook)
            getBooksFromCart()

        },[])






    return(
        <Box sx={{
            width:'100vw',
            height:'400vh',
            display:'flex',
            flexDirection:'column',
            alignItems:'center',


        }}>
            <Box>
                <Header/>
            </Box>
            <Paper sx={{
                width:'70%',
                position:'relative',


            }}>
            <Box sx={{
                     display:'flex',
                     flexDirection:'row',
                     position:'relative',
                     top:30,

                     width:'60%',
                     marginBottom:4,

                 }}>
            <Typography  sx={{ position:'relative',
                    left:100,}}> My Cart</Typography>
            <Box  sx={{width:'50%',
                    height:'100%',


                    display:'flex',
                    flexDirection:'column'}}>

                        <Box sx={{ width: '60%',position:'relative',left:700
                        }}>
                            <FormControl sx={{width:'65%'}}>
                                <InputLabel id="demo-simple-select-label">location</InputLabel>
                                <Select

                                >
                                <MenuItem value={10}>Ten</MenuItem>
                                <MenuItem value={20}>Twenty</MenuItem>
                                <MenuItem value={30}>Thirty</MenuItem>
                                </Select>
                            </FormControl>
                            </Box>

                    </Box>
            </Box>
            <Paper sx={{
                 width:'90%',
                display:'flex',
                flexDirection:'column',


            }}>
                <Box sx={{ display:'flex',
                            flexDirection:'row'}}>

                <Grid sx={{
                    width:'80%',
                    left:30
                }}>
                {
                    bookCart.map((book)=>(
                    <Cartlist book={book}/>))
                }


            </Grid>
            {
                !displayAddress? null:

            <Button sx={{
                width:180,
                postion:'relative',
                top:'97%',
                left:35,
                height:50,
                border:'1px solid blue',
                background:'blue',
                color:'white'
            }} onClick={customerDetails}>Place Order</Button>
        }
            </Box>


            </Paper>
            {
                displayAddress?

                     <Card sx={{width:'80%', height:80,
                     position:'relative',
                    left:5,top:10}}
                    >   
                    <Typography sx={{display:'flex',justifyContent:'flex-start',
                     postion:'absolute',left:100,top:100,}}>
                        Address Details
                    </Typography>
                      </Card>
                      :
                      <Card sx={{width:'100%',height:'15%'}}>
                          <AddressDetails/>
                      </Card>
            }

            </Paper>


        </Box>
        )

}


export default Cart
