import React from "react";
import Box from '@mui/material/Box';
import zIndex from "@mui/material/styles/zIndex";
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import { border, textAlign } from "@mui/system";
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import '../signup/signup.css'
import { useThemeProps } from "@mui/material";


function RedBar() {
    return (
      <Box
        sx={{
          height: 3
        }}
      />
    );
  }






function Signup(props){

  //state to initialiaze labels in loob back
  const[allform, setallform]=React.useState({ firstName:"",email:"", password:"" ,service:"advance"})
  const emailRegex =  /^[a-zA-Z]+[a-zA-Z0-9]*[- . + _]?[a-zA-Z0-9]+[@]{1}[a-z0-9]+[.]{1}[a-z]+[.]?[a-z]+$/
  const passwordRegex =  /^(?=.*[A-Z])(?=.*[0-9])(?=.*[@#$%^&-+=()])([a-zA-Z0-9]*).{8,}$/
  const fullNameRegex = /[A-Z]{1}[a-z]{2,}/
  const mobileNumber= /^([0|\+[0-9]{1,5})?([7-9][0-9]{9})$/


  function takefname(event){
    setallform({...allform,firstName:event.target.value})

}

  function takeeemail(event){

    setallform({...allform,email:event.target.value})
}

  function takepwd(event){
    setallform({...allform,password:event.target.value})

}



  const signIn=()=>{
    props.listenToSignUp()
  }

  let fnameTest=fullNameRegex.test(allform.fullName)
  let emailTest=emailRegex.test(allform.email)
  let pwdTest=passwordRegex.test(allform.password)
  //to see the result gotot console either true or false
  console.log(fnameTest)
  console.log(emailTest)
  console.log(pwdTest)


    return(


             <Box sx={{
              top: 166,
              left: 658,
              width: 400,
              height: 440,
              background: '#FFFFFF 0% 0% no-repeat padding-box',
              boxShadow: '0px 5px 15px #00000029',
              border: '1px solid #E4E4E4',
              opacity: 1,
              zIndex:4,
              display:'flex',
              flexDirection:'column',
              alignItems:'start'
                 }}>
                     <Box sx={{
                         position:'relative',
                         top: 5,
                         width: '40%',
                         height: 33,
                         textAlign: 'left',
                         font: 'normal normal medium 25px/33px Roboto',
                         letterSpacing: 0,
                         color: '#0A0102',
                         textTransform:'uppercase',
                         opacity: 1,
                         display:'flex',
                         flexDirection:'row',
                         justifyContent:'space-even',
                         boxShadow:2



                     }}><h2 className='signup'onClick={signIn} sx={{color:'grey'}}>LOGIN</h2>
                        <h2 className='login'  >SIGNUP</h2>

                     </Box>
                     <Box
                        sx={{
                            display: "flex",
                            flexDirection: "column",
                            "& .MuiTextField-root": { width: "30ch" },
                            position:'relative',
                            left:50,
                            top:30,

                        }}
                        >
                        <RedBar />
                        <TextField label={'Full-Name"'} id="margin-none"
                         helperText="enter full name"
                         onChange={takefname}
                         //error={fnameerror}
                        // helperText={fnamehelper}
                          />


                        <RedBar />
                        <TextField label={'Email Id'} id="margin-none"  
                         helperText="enter email id"
                        className="emailid"
                        onChange={takeeemail}
                        //error={emailerror1}
                        //helperText={emailhelper1}
                        />


                        <RedBar />
                        <TextField label={'Password'} id="margin-normal"
                         helperText="password"
                         onChange={takepwd}
                        // error={pwderror1}
                         htmlFor="outlined-adornment-password"
                         //helperText={pwdhelper1}

                        />


                        <RedBar />
                        <TextField label={'Mobile Number'} 
                        id="margin-normal"
                        helperText="mobile" />

                        <Button variant="contained"  style={{position:'relative',background: '#A03037 0% 0% no-repeat padding-box', width:270}}
                         >Signup</Button>



                     </Box>





             </Box>


    )

}
export default Signup
