import React from "react";
import { Box } from "@mui/system";
import image from '../Books/images/book.png'
import Card from '@mui/material/Card';
import { Typography } from "@mui/material";
import {postCart,getCart} from '../../Services/dataservice'
import {  Button,   Rating, InputBase } from "@mui/material";

function Book(props){

    const [cuurentCartItem, setCurrentItem] = React.useState({})
    const [filterArray,setFilterArray]=React.useState([])
    const [shop,setShop]=React.useState([])
    const [data,setData]=React.useState('book')


//let filterarray = data.fileter()

const addCart=()=>{
    console.log(props.currentBook._id, props)
    postCart(props.currentBook._id).then((response)=>{

        console.log(response)
        setData('cart')
    }).catch((error)=>{
        console.log(error)
    })
}


const getBookFromSwagger=(data)=>{

    getCart().then((response)=>{

        setShop(response.data.result)
        console.log(shop)
        if(data==='cart'){

            let filter=response.data.result.filter(function(item){
                if(props.currentBook._id===data.result.product_id._id){
                    //return(item)
                    console.log(item)
                }
                else{
                    console.log("not in cart")
                }

            })

        }



    }).catch((error)=>{

        console.log(error)

    })


}
React.useEffect(()=>{
    //console.log(getBook)
    getBookFromSwagger()

},[data])


    return(
        <Box sx={{
            position:'relative',
            top:50,

            width:'90vw',
            height:'90vh',
            backgroundColor:'white',

            display:'flex',
            flexDirection:'row',
            justifyContent:"center",
            alignItems:'center',position:'relative',
            left:'3%'

        }}>
            <Box sx={{width:'30%',height:'90%',position:"relative",display:'flex',flexDirection:'column',
 alignItems:'center', justifyContent:'flex-start', top:40,
        left:100, }}>
            <Box sx={{width:'100%',height:'50%',position:"relative",display:'flex', boxShadow: '2px 2px 2px 2px gray',
              justifyContent:'center',alignItems:'center',bottom:70
        }}>
            <img src={ image} style={ {width:'60%',height:'80%'}}/>
            </Box>
            <Box sx={{display:'flex', width:'100%',height:'5%',flexDirection:'row',justifyContent:'space-around',position:'relative',bottom:'5%',border:'1px solid yellow'} }>
                <button style={{width:'20%',background:'#A03037'}} onClick={addCart}>cart</button>
                <button style={{width:'20%',background:'black',color:'white'}}>whistlist</button>

            </Box>


            </Box>


            <Box sx={{width:'65%',height:'80%'
            ,position:'relative',
            display:'flex',flexDirection:'column',
            justifyContent:'flex-start',marginLeft:20 ,marginBottom:4,bottom:50}}>

                <Card sx={{ width:'100%%', postion:'relative',
            height:'200px', display:'flex',flexDirection:'column', alignItems:'flex-start', boxShadow:' , , , 10',top:500,marginBottom:4
            }}>
                <Typography sx={{fontSize:40,
                                position:'relative',marginTop:3,

                }}>{props.currentBook.bookName}</Typography>
                <Typography sx={{color:'gray',fontSize:20,marginBottom:1}}>
                {props.currentBook.author}
                </Typography>
                <Typography sx={{background:'#43a047',color:'white',marginBottom:1}}>4.3 *</Typography>
                <Typography sx={{fontSize:25,marginBottom:2}}>{props.currentBook.price}</Typography>


                </Card>
                <Card sx={{ width:'100%%', postion:'relative',
                     height:'200px', display:'flex',flexDirection:'column', alignItems:'flex-start', boxShadow:' , , , 10',marginBottom:4
            }}>
                <Typography > {props.currentBook.description}
                </Typography>


                </Card>
                <Card sx={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start', height: '20%', borderRadius: '10px' }} style={{ background: '#F5F5F5' }}>
                    <Typography>Overall rating</Typography>
                    <Rating name="no-value" value={null} />
                    <Box className="reviewbox">
                        <InputBase placeholder='Write Your Review' sx={{ width: '100%' }}></InputBase>
                    </Box>
                    <Box className="buttonbox">
                        <Button variant="contained" size="small">Submit</Button>
                    </Box>
                </Card>

            </Box>



        </Box>
    )
}
export default Book
