import React from "react";
import Box from '@mui/material/Box';
import Header from "../../components/Header/header";
import {getBooks} from '../../Services/dataservice'
import Books from "../../components/Books/Books";
import Grid from '@mui/material/Grid';
import ImageList from '@mui/material/ImageList';
import Book from "../../components/Book/Book";
import { TryRounded } from "@mui/icons-material";
import { PinDropSharp } from "@material-ui/icons";
import { border, typography } from "@mui/system";
import '../Cart/Cart.css'
import { Button, Card } from "@mui/material";
import { styled } from '@mui/material/styles';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import {getCart} from '../../Services/dataservice'
import { OneKPlusOutlined } from "@mui/icons-material";



function Cart(){

    const [cart,setCart]=React.useState([])

    const getBookFromCart=(data)=>{

        getCart().then((response)=>{
            let a=response.data.result.product_id
            //console.log(ad)

          })
             .catch((error)=>{
    console.log(error)

        })
     }



     React.useEffect(()=>{
        //console.log(getBook)
        getBookFromCart()


    },[])

    return(
        <Box sx={{
            width:'100vw',
            height:'100vh',
            display:'flex',
            flexDirection:'column'

        }}>
            <Box>
                <Header/>
            </Box>
            <Box className="body">
                <Card sx={{

                    width:'100%',
                    height:350,marginBottom:2,
                    display:'flex',
                    flexDirection:'row',
                    justifyContent:'flex-start',
                    alignItems:'flex-start'
                }}>
                        <Box  sx={{width:'50%',
                    height:'100%',

                    display:'flex',
                    flexDirection:'column',alignItems:"flex-start"
                    }}>
                            <Typography >
                                My Cart
                            </Typography>
                            <Typography sx={{marginTop:2,width:'100%',height:'100%',background:'yellow'}}> 


                            </Typography>

                    </Box>
                     <Box  sx={{width:'50%',
                    height:'100%',

                    display:'flex',
                    flexDirection:'column'}}>

                        <Box sx={{ width: '60%',position:'relative',left:325 }}>
                            <FormControl sx={{width:'65%'}}>
                                <InputLabel id="demo-simple-select-label">location</InputLabel>
                                <Select

                                >
                                <MenuItem value={10}>Ten</MenuItem>
                                <MenuItem value={20}>Twenty</MenuItem>
                                <MenuItem value={30}>Thirty</MenuItem>
                                </Select>
                            </FormControl>
                            </Box>
                            <Box>
                                <Button sx={{position:'relative',
                                            top:200,
                                            left:200,
                                            width:150,
                                            height:50,
                                            background:'blue',
                                            color:'white'
                            }}>place order</Button>
                            </Box>
                    </Box>



                </Card>


                <Card   sx={{

                    width:'100%',
                    height:130,
                    marginBottom:2,

                }}

                > 
                <Typography sx={{display:'flex',justifyContent:'flex-start',postion:'relative',left:100,
                             top:50 }}>Address Details</Typography></Card>
                <Card sx={{

                    width:'100%',
                    height:130,
                    marginBottom:2,}}
                >
                    <Typography sx={{display:'flex',justifyContent:'flex-start',
                     postion:'absolute',left:100,top:100}}>
                        OrderSummary
                    </Typography>
                </Card>


            </Box>
        </Box>
            )
}


export default Cart
