import React from "react";
import Box from '@mui/material/Box';
import Header from "../../components/Header/header";
import Cartlist from "../../components/Cartlist/Cartlist";
import {getCart} from '../../Services/dataservice'
import { OneKPlusOutlined } from "@mui/icons-material";
import { Grid,Paper,Typography,Card,Button } from "@mui/material";
import FormControl from '@mui/material/FormControl';
import MenuItem from '@mui/material/MenuItem';
import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';
import AddressDetails from "../../components/AdressDetails/AdressDetails";
import {order} from "../../Services/dataservice";
import Book from "../../components/Book/Book";
import OrderSummary from "../../components/OrderSummary/OrderSummary";
import { TrendingUpRounded } from "@mui/icons-material";


function Cart(){
    const [bookCart,setBookCart]=React.useState([])
    const [displayAddress, setDisplayAddress]=React.useState([true])
    const [orderdetail,setOrderDetails]=React.useState({orders:[]})
    const [displayOrder, setDisplayOrder]=React.useState(false)


    const listentoContinue=()=>{
        setDisplayOrder(true)
        setDisplayAddress(true)


    }


    const customerDisplay=()=>{
        setDisplayAddress(false)

    }                                                 

    const enterOrderDetails=()=>{
        console.log('hi')

        let ordersArray = bookCart.map(function(book) {
            let obj = {
                "product_id": book.product_id._id,
      "product_name": book.product_id.bookName,
      "product_quantity": book.quantityToBuy,
      "product_price": book.product_id.discountPrice
            }
            return obj
        })
        setOrderDetails({...orderdetail,orders : ordersArray})


        order(orderdetail).then((response)=>{
            console.log(response)

        }).catch((error)=>{
            console.log(error)
        })

    }

    const getBooksFromCart=()=>{
        getCart().then((response)=>{

                setBookCart(response.data.result)

            }).catch((error)=>{

                console.log(error)

            })

        }
        React.useEffect(()=>{
            //console.log(getBook)
            getBooksFromCart()

        },[orderdetail])






    return(
        <Box sx={{
            width:'100vw',
            height:'400vh',
            display:'flex',
            flexDirection:'column',
            alignItems:'center',


        }}>
            <Box>
                <Header/>
            </Box>
            <Paper sx={{
                width:'70%',
                position:'relative',


            }}>
            <Box sx={{
                     display:'flex',
                     flexDirection:'row',
                     position:'relative',
                     top:30,

                     width:'60%',
                     marginBottom:4,

                 }}>
            <Typography  sx={{ position:'relative',
                    left:100,}}> My Cart</Typography>
            <Box  sx={{width:'50%',
                    height:'100%',


                    display:'flex',
                    flexDirection:'column'}}>

                        <Box sx={{ width: '60%',position:'relative',left:700
                        }}>
                            <FormControl sx={{width:'65%'}}>
                                <InputLabel id="demo-simple-select-label">location</InputLabel>
                                <Select

                                >
                                <MenuItem value={10}>Ten</MenuItem>
                                <MenuItem value={20}>Twenty</MenuItem>
                                <MenuItem value={30}>Thirty</MenuItem>
                                </Select>
                            </FormControl>
                            </Box>

                    </Box>
            </Box>
            <Paper sx={{
                 width:'100%',
                display:'flex',
                flexDirection:'column',


            }}>
                <Box sx={{ display:'flex',
                            flexDirection:'column'}}>

                <Grid sx={{
                    width:'95%',
                    marginBottom:10,marginTop:5

                }}>
                {
                    bookCart.map((book)=>(
                    <Cartlist book={book}/>))
                }


            </Grid>
            {
                !displayAddress? null:

            <Button sx={{
                width:150,
                postion:'relative',
                bottom:15,
                left:'50%',
                height:50,
                border:'1px solid blue',
                background:'blue',
                color:'white'
            }} onClick={customerDisplay} >Place Order</Button>
        }
            </Box>


            </Paper>
            {
                displayAddress?

                     <Box sx={{width:'100%', height:20,
                     position:'relative',marginTop:1,border:'1px solid black'
                    }}
                    >   
                    <Typography sx={{display:'flex',justifyContent:'flex-start',
                     postion:'absolute',left:100}}>
                        Address Details
                    </Typography>
                      </Box>
                      :
                      <Card sx={{width:'100%',height:350, position:'relative',marginTop:2,marginBottom:2}}>
                          <AddressDetails listentoContinue={listentoContinue}/>
                      </Card>
            }



            {
                displayOrder?

                    <Paper  sx={{
                        width:'100%',
                       display:'flex',
                       flexDirection:'column',


                   }}> 
                          <Grid sx={{
                                width:'95%',


                            }}>
                            {
                                    bookCart.map((book)=>(
                                    <OrderSummary book={book}/>))
                                }


                        </Grid>

                        <Button onlcick={enterOrderDetails} style={{background:'blue',width:100,color:"white",
                                                                    position:'relative',left:500}}>Checkout</Button>
                        </Paper>

                      :
                      <Box sx={{width:'100%', height:20,
                     position:'relative',marginTop:1,border:'1px solid black'
                    }}
                    >   
                    <Typography sx={{display:'flex',justifyContent:'flex-start',
                     postion:'absolute',left:100,}}>
                        Order Details
                    </Typography>
                      </Box>

            }

            </Paper>


        </Box>
        )

}


export default Cart
